performPairwiseTTest <- function(dependentVariable, independentVariable, dataset, varianceEqual = "TRUE", paired = "FALSE", independentVariableName, levelA, levelB){    dataset <- as.data.frame(dataset);        dependentVariable <- c(dependentVariable);    independentVariable <- c(independentVariable);        result = pairwise.t.test.with.t(dependentVariable, independentVariable, p.adjust="bonferroni", var.equal=T, paired=F);        levels = eval(parse(text = paste("sort(unique(dataset$",independentVariableName,"))",sep="")));        p = result[["p.value"]];    pVal = 0;        if(levelA == levels[length(levels)])    {        pVal = p[levelA, levelB];    }    else    {        if(levelA < levelB)        {            pVal = p[levelB, levelA];        }        else        {            pVal = p[levelA, levelB];        }    }         t = result[["t.value"]];    tVal = 0;        if(levelA == levels[length(levels)])    {        tVal = p[levelA, levelB];    }    else    {        if(levelA < levelB)        {            tVal = p[levelB, levelA];        }        else        {            tVal = p[levelA, levelB];        }    }         list(p = pVal, t = tVal);}