performSphericityTest <- function(dependentVariable, withinGroupVariable, betweenGroupVariable = "null", participantVariable, dataset){       install.packages("ez");    library(ez);            table <- as.data.frame(dataset);        if(betweenGroupVariable == "null")        result <- eval(parse(text = paste("ezANOVA(table,",dependentVariable,",",participantVariable,",within=", withinGroupVariable,")",sep="")));    else        result <- eval(parse(text = paste("ezANOVA(table,",dependentVariable,",",participantVariable,",within=", withinGroupVariable,"between=",betweenGroupVariable,")",sep="")));    result <- result[["Mauchly's Test for Sphericity"]];        list(p = result$p);    }